styles:
/*
    Header Styles
*/

.header {
    margin: 20px 24px 12px 24px;
}

.header:hover{
    cursor: pointer;
    background: rgba(33,33,33,0.05);
}

.header.collapsed{
    margin:0;
}

.header button {
    background: transparent;
    border: 0;
    height: 40px;
    padding: 0;
    width: 40px;
}

.header button:before {
    color: #333333;
}

.header-content {
    padding-left: 10px;
}

.header.collapsed .header-content {
    padding: 0 20px 0 34px;
}

/*
    Content Area Styles
*/

.content {
    margin-bottom: 12px;
    overflow: auto;
    height: 30vh;
    max-height: 600px;
    min-height: 150px;
}

.content.collapsed {
    display: none;
}

/*
    Header Title Styles
*/

.header h5 {
    color: #333333;
    font-weight: bold;
    padding: 0;
    line-height:40px;
}

