styles:
.pane {
    position: absolute;
    top: 0;
    bottom: 0;
    right: 0;
}

.pane.full-screen .modal-content {
    width: 100%;
    border: 0px;
}

.pane .modal-content {
  height: 100%;
  width: 520px;
  overflow-y: auto;
  margin: 0px;
  border-top-width: 0px;
  border-bottom-width: 0px;
  border-right-width: 0px;
}

.modal-content {
  padding: 0;
}

.modal-dialog .modal-body {
  max-height: 540px;
  overflow-y: auto;
  overflow-x: hidden;
}

.modal-body {
  margin: 0px 0px 0px 36px;
  padding: 0px 36px 0px 0px;
}

.modal-header {
  padding: 24px 36px 0 36px;
}

.modal-footer {
  padding: 0 36px 24px 36px;
}
:host >>> .modal-footer .btn {
  width: auto;
  margin: 4px 8px 0px 0px
}

:host >>> h1,
:host >>> h2,
:host >>> h3,
:host >>> h4,
:host >>> h5,
:host >>> h6 {
  padding-top: 0;
  padding-bottom: 36px;
}

:host >>> p {
  padding-top: 0;
  padding-bottom: 15px;
}

/*:host >>> .form-group .form-group-label,
:host >>> .form-group label {
  font-size: 13px;
}*/

:host >>> .dialog-scrollable-content {
  max-height: 30vh;
  overflow-y: auto;
  margin-bottom: 2px;
}

:host.no-footer .modal-content .modal-footer {
  display: none;
}

:host >>> .list-dialog-header,
:host >>> .list-dialog-footer {
  padding-bottom: 0;
}

