styles:
:host>>>.modal-body {
  height: 675px !important;
}

:host>>>sme-backdrop {
  /* this addresses a strange bug in edge that casuese scroll bars to appear do to a rendering problem with the transform(translate.... on the dialogs 'relative-center' class */
  overflow: hidden !important;
}

:host>>>.modal-dialog {
  width: 800px !important;
  max-width: 98vw !important;
  margin: 0 !important;
}

:host>>>.modal-content {
  margin: 0px !important;
}

:host>>>.modal-dialog .modal-content .modal-body {
  max-height: 98vh !important; /* makes the dialog scroll if the screen is not big enough to accomodate for 800px height dialog*/
  margin: 0px !important;
  padding: 0px !important;
}

:host>>>h4 {
  color: #0078D7;
  padding-bottom: 12px !important;
}

:host>>>p {
  padding-bottom: 0px !important;
}

:host>>>.btn {
  background: white;
  color: black;
  margin: 0 !important;
}

:host>>>.btn.btn-primary {
  background: white;
  color: #0078D7;
  margin: 0;
}

:host>>>.btn.btn-primary:hover,
:host>>>.btn.btn-primary:focus {
  background: white;
  color: #0078D7;
  text-decoration: underline;
}

:host>>>.btn:hover,
:host>>>.btn:focus {
  background: white;
  color: black;
  text-decoration: underline;
}

.center {
  text-align: center;
}

.dialog-media {
  height: 100vh;
  width: 100vw;
  max-height: 500px;
  max-width: 800px;
  padding-bottom: 10px;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
}

@media screen and (max-height: 700px) {
  .dialog-media {
    height: 65vh;
    min-height: 250px;
  }
}

:host>>>.modal-header {
  padding: 0 !important;
}

.description {
  margin-right: 24px;
  margin-left: 24px;
}

:host>>>.carousel-indicators .active {
  background-color: #0078D7;
  border: 0px;
  margin: 5px;
  width: 10px;
  height: 10px;
}

:host>>>.carousel-indicators li {
  border: 1px solid #0078D7;
  margin: 5px;
}

.carousel-indicators {
  position: relative;
  bottom: 0px;
  margin-bottom: 0px;
}

