styles:
:host >>> p,
:host >>> span,
:host >>> h4,
:host >>> h5 {
  color: #ffffff;
}

:host >>> .pane .modal-content {
  width: 384px!important;
  background: #414141;
  overflow-x: hidden;
}

:host >>> .pane .modal-body,
:host >>> .pane .modal-header {
  padding: 0px;
  margin-left: 0px;
}

.alert {
  padding-bottom: 0px;
}

.notification {
  background: #414141;
}

.notification-body {
  padding-top: 0px;
  padding-left: 20px;
  padding-right: 0px;
  margin-bottom: 0px;
}

.notification-header {
  padding-left: 20px;
  padding-bottom: 15px;
  padding-top: 15px;
}

.clear-all {
  font-size: 13px;
  line-height: 16px;
  padding-top: 36px;
  padding-bottom: 0px;
  padding-right: 20px;
}

.details-row {
  height: 16px;
}

.title-row {
  height: 36px;
}

.title-padding {
  padding-left: 5px;
}

.icon-size{
  padding-top: 5px;
  color: #ffffff;
  width: 36px;
}

.notification.selectable:hover {
  background: #3f3f3f;
  cursor: pointer;
}

.close-button {
  display: none;
  padding-right: 0px;
  color: #ffffff
}

.notification.selectable:hover .close-button {
  display: block;
  padding-right: 0px;
}

.notification-progress {
  padding-top: 0px;
  padding-bottom: 0px;
  padding-left: 0px;
  padding-right: 0px;
  margin-bottom: 0px;
  height: 16px;
}

.percent-progress {
  width: 100%;
  background: #262626
}

.notification.selectable:hover .percent-progress {
  background: #3f3f3f
}

.indeterminate-progress {
  background: #262626;
  box-shadow: none;
  padding-bottom: 10px;
}

.notification.selectable:hover .indeterminate-progress {
  background: #3f3f3f
}

.notification-details {
  font-size: 11px;
  line-height: 15px;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
}

.notification-title {
  font-size: 13px;
  line-height: 16px;
  width: 280px;
  padding-top: 10px;
  padding-bottom: 10px;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
}

.notification-tab {
  padding-left: 25px;
  padding-bottom: 0px;
}

.time {
  padding-right: 36px;
  padding-left: 12px;
  width: 160px;
}

.notification-details-body {
  padding-right: 36px;
}

.details-l {
  font-size: 20px;
  line-height: 24px;
  padding: 0px 0px 8px 48px;
}

.details-s {
  font-size: 13px;
  line-height: 16x;
  padding: 0px 0px 4px 48px;
}

.go-to-tool {
  padding-top: 20px;
  padding-bottom: 40px;
}

.details-icon-div {
  width: 48px;
  text-align: center;
  margin-right: 0px;
  height: 28px;
}

.details-icon {
  vertical-align: middle;
  font-size: 20px;
  height: 20px;
  line-height: 28px;
}

.details-title {
  padding-left: 0px;
  width: 348px;
}

.back-button {
  cursor: pointer;
  height: 48px;
  width: 48px;
  vertical-align: middle;
  color: #ffffff
}

.back-button:hover {
  background-color: #3f3f3f
}

.back-button-div {
  width: 48px;
  height: 48px;
  padding-top: 12px;
  padding-bottom: 12px;
  color: #ffffff;
  text-align: center;
  margin-right: 300px;
}

