styles:
.tools-nav {
    background: white;
    color: #262626; 
    min-width: 150px;
    max-width: 350px;
    height:100%;
}

.tools-nav.collapsed {
    width: 55px;
    min-width: initial;    
    overflow:hidden;    
}

.tools-nav-title {
    font-size: 20px;
    font-weight: 600;
    padding-left: 15px;  
    overflow:hidden;  
    height:44px;
    color: #333;
}

.tools-nav.collapsed .tools-nav-title{
    padding-left:0px;
}

.tools-nav-title .text {
    line-height:44px;
}
.tools-nav.collapsed .tools-nav-title .text{
    display:none;
}

.tool.search-button {
    height:42px;
    display:none;
    cursor: pointer;
}

.tools-nav.collapsed .tool.search-button{
    display:block;    
}

.searchbox {
    margin: 6px 15px;    
    position:relative;
}

.searchbox .tool-icon {
    line-height:44px;
}

.tools-nav.collapsed .searchbox{
    border:none;
    margin-left:0;
    padding-left:0; 
}

.tools-nav.collapsed .searchbox input{
    background:transparent;
    height:36px;
    cursor: pointer;    
}

.split-view-toggle {
    background: transparent;
    border: none;
    width:44px;
    height:44px;
    font-size:19px;
    padding: 8px;
}

.tools-nav.collapsed .split-view-toggle{
    margin:0;
    padding:0;
    width:100%;
}

.tools-nav.collapsed nav{
    margin-right:-50px;
}

.tool {
    align-content: center;
    align-items: center;
    height: 36px;
    width: 100%;
    cursor: pointer;
    white-space: nowrap;
    font-size: 12px;
    user-select: none;
    padding: 0 15px 0 5px;
    color: #262626;
    border-left-width: 5px;
    border-left-style: solid;
    border-left-color: transparent;
}

.tool:hover, .tool:focus, .tool.active {
    background: #F2FBFE;
    color: black;
}

.tool:hover, .tool:focus {
    text-decoration: underline;
}

.tool:focus{
    outline: black dashed 1px;
    outline-offset: -1px;    
}

.tool.active {
    background: #E6F7FE;
    border-left-color: #327cd4;    
}

.tool.sideloaded {
    color: #f60;
    font-weight: bold;
}

.tool.sideloaded.active {
    border-left-color: #f60;
    color: #f60;
}

.tool-label {
    display: inline-block;
    font-size: 13px;
    margin-right: 15px;
}
.tools-nav.collapsed .tool-label {
    display:none;    
}

.tool-title{
    width: 227px;
    overflow: hidden;
    text-overflow: ellipsis;
}

.tool-icon {
    height: 36px;
    width: 36px;
    min-width:36px;
    box-sizing: content-box;
    background-size: 18px;
    background-position: center;
    background-repeat: no-repeat;
    display: inline-block;
    font-size: 14px;
    text-align: center;
    line-height: 36px;
    vertical-align: middle;
}

.keywords{
    font-size: 10px;
    font-style: italic;
    font-weight: 300;
}

